CREATE FUNCTION public.func_fetch_comments(_post_id uuid, _limit integer, _offset integer) RETURNS TABLE(id uuid, content text, created_at timestamp with time zone, parent_comment_id uuid, author jsonb)
    LANGUAGE plpgsql
    AS $$
BEGIN
  RETURN QUERY
  SELECT
    c.id,
    c.content,
    c.created_at,
    c.parent_comment_id,
    jsonb_build_object(
      'id', a.id,
      'name', a.name,
      'username', a.username,
      'avatar', a.avatar
    ) AS author
  FROM post_comments c
  JOIN users a ON c.user_id = a.id
  WHERE c.post_id = _post_id
  ORDER BY c.created_at DESC
  LIMIT _limit
  OFFSET _offset;
END;
$$;


