--
-- Name: func_fetch_posts(integer, public.post_category, text[], text, text, integer, integer); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public.func_fetch_posts(_current_user_id integer, _category public.post_category, _tag_names text[], _search_query text, _sort_by text, _limit integer, _offset integer) RETURNS TABLE(id uuid, title text, content text, image text, category public.post_category, views integer, created_at timestamp with time zone, likes_count integer, comments_count integer, shares_count integer, tags text[], author jsonb, quiz jsonb, syllabus jsonb, is_liked boolean, bookmarked boolean)
    LANGUAGE plpgsql
    AS $$
BEGIN
  RETURN QUERY
  WITH posts_with_tags AS (
    SELECT
      p.*,
      COALESCE(array_agg(t.name) FILTER (WHERE t.name IS NOT NULL), '{}') AS tag_names_array
    FROM posts p
    LEFT JOIN post_tags pt ON p.id = pt.post_id
    LEFT JOIN tags t ON pt.tag_id = t.id
    GROUP BY p.id
  )
  SELECT
    p.id, p.title, p.content, p.image, p.category, p.views, p.created_at,
    p.likes_count, p.comments_count, p.shares_count, p.tag_names_array AS tags,
    

    jsonb_build_object(
      'id', a.id,
      'name', a.name,
      'email', a.email,
      'avatar', a.user_image,
      'isFollowing', (EXISTS (
        SELECT 1 FROM user_followers uf 
        WHERE uf.follower_id = _current_user_id AND uf.following_id = a.id
      ))
    ) AS author,
    
    -- SỬA LỖI: Thay thế 'FILTER' bằng 'CASE'
    CASE
      WHEN q.id IS NOT NULL THEN
        jsonb_build_object(
            'id', q.id, 
            'title', q.title, 
            'questionCount', q.question_count, 
            'difficulty', q.difficulty
        )
      ELSE
        NULL -- Trả về NULL nếu không có quiz
    END AS quiz,
    
    -- SỬA LỖI: Thay thế 'FILTER' bằng 'CASE'
    CASE
      WHEN s.id IS NOT NULL THEN
        jsonb_build_object(
            'id', s.id, 
            'title', s.title, 
            'lessonCount', s.lesson_count, 
            'duration', s.duration
        )
      ELSE
        NULL -- Trả về NULL nếu không có syllabus
    END AS syllabus,
    
    EXISTS (SELECT 1 FROM post_likes pl WHERE pl.user_id = _current_user_id AND pl.post_id = p.id) AS is_liked,
    EXISTS (SELECT 1 FROM user_bookmarks ub WHERE ub.user_id = _current_user_id AND ub.post_id = p.id) AS bookmarked
    
  FROM posts_with_tags p
  JOIN users a ON p.user_id = a.id
  LEFT JOIN quiz q ON p.quiz_id = q.id
  LEFT JOIN syllabus s ON p.syllabus_id = s.id
  
  WHERE
    (_category IS NULL OR p.category = _category)
    AND (_tag_names IS NULL OR array_length(_tag_names, 1) IS NULL OR p.tag_names_array @> _tag_names)
    AND (_search_query IS NULL OR _search_query = '' OR
         p.title ILIKE ('%' || _search_query || '%') OR
         p.content ILIKE ('%' || _search_query || '%') OR
         a.name ILIKE ('%' || _search_query || '%')
    )
  ORDER BY
    CASE WHEN _sort_by = 'recent' THEN p.created_at ELSE NULL END DESC,
    CASE WHEN _sort_by = 'popular' THEN p.likes_count ELSE NULL END DESC,
    CASE WHEN _sort_by = 'trending' THEN (p.likes_count + (p.comments_count * 2) + (p.shares_count * 3)) ELSE NULL END DESC,
    p.created_at DESC
  LIMIT _limit
  OFFSET _offset;
END;
$$;


