CREATE FUNCTION public.func_toggle_bookmark(_user_id uuid, _post_id uuid) RETURNS boolean
    LANGUAGE plpgsql
    AS $$
DECLARE
  is_bookmarked BOOLEAN;
BEGIN
  IF EXISTS (SELECT 1 FROM user_bookmarks WHERE user_id = _user_id AND post_id = _post_id) THEN
    DELETE FROM user_bookmarks WHERE user_id = _user_id AND post_id = _post_id;
    is_bookmarked := FALSE;
  ELSE
    is_bookmarked := TRUE;
  END IF;
  RETURN is_bookmarked;
END;
$$;


